import{_ as e,c as t,o as a,a4 as r}from"./chunks/framework.CzsDe1xi.js";const m=JSON.parse('{"title":"客户端","description":"","frontmatter":{},"headers":[],"relativePath":"src/frame/sv-client/sv-client.md","filePath":"src/frame/sv-client/sv-client.md","lastUpdated":1714466133000}'),s={name:"src/frame/sv-client/sv-client.md"},o=r('<h1 id="客户端" tabindex="-1">客户端 <a class="header-anchor" href="#客户端" aria-label="Permalink to &quot;客户端&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">请注意</p><p>使用 uniapp 开发时，由于 uni 内部已封装好页面路由模块(pages.json，类似于微信小程序开发)，而且不借助外力(路由插件)的情况下，并不能使用类似于 vue-router 形式来写，并且市面上绝大多数可以模拟 vue-router 书写方式的路由插件也无法提供 router-view，而且 App.vue 中常规下无法使用 template 标签，因此几乎无法像普通 vue 开发项目在 App.vue 中使用 router-view 来开设公共区域以供整个项目共享同一个组件例如头部导航栏或者底部菜单栏，所以在客户端中 <code>components/sv-page</code> 这一页面级组件充当了这一功能，以组件复用的写法来实现公共的页面布局、主题换肤等，但是本质上并非像 router-view 那样真正的共用同一组件，但是可能这也是目前唯一相对较好的方案了，当然如若有建议还望反馈我，谢谢。(此处先不讨论部分优秀的但是需要部分付费的路由插件例如 v3 版 <a href="https://www.hhyang.cn" target="_blank" rel="noreferrer">Uni Simple Router</a>，如有错误还望指正)</p></div>',2),c=[o];function n(i,l,p,_,d,u){return a(),t("div",null,c)}const h=e(s,[["render",n]]);export{m as __pageData,h as default};
